sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

	if (action == "insert") {
		target.setNewGuidValue(source.x_carr5_cds_imp_fed_rec_sysid);
	}

	if(Source.x_carr5_cds_imp_global_class == "x_carr5_coredata_ship"){
		target.sys_class_name = "x_carr5_coredata_ship";
	}
	else if(Source.x_carr5_cds_imp_global_class == "x_carr5_coredata_office"){
		target.sys_class_name = "x_carr5_coredata_office";
	}
	else if(Source.x_carr5_cds_imp_global_class == "x_carr5_coredata_port"){
		target.sys_class_name = "x_carr5_coredata_port";
	}
	else{
		target.sys_class_name = "x_carr5_coredata_location";
	}



})(source, map, log, target);
