sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

    var x_carr5_coredata_data_status = "";
    var x_carr5_coredata_record_status = "";

    var fedPubUtil = new x_carr5_core_fed.coreFedPubUtil();
    var isMasterInstance = fedPubUtil.isFedMasterInstance;

    if (isMasterInstance == true && source.x_carr5_cds_imp_created_source == "Fed-Import Automation") {

        source.x_carr5_cds_imp_data_status = 'sync';
        source.x_carr5_cds_imp_record_status = 'sync';

    }


})(source, map, log, target);
